package main

import (
	"fmt"
	"testing"

	"github.com/stretchr/testify/assert"
)

func TestEncrypt(t *testing.T) {

	signer := rightPad2Len("sigadfasdner", "=", 32)
	encrypted, err := Encrypt(signer, []byte(""))
	assert.NoError(t, err)

	res, err := Decrypt(signer, encrypted)
	assert.NoError(t, err)

	fmt.Println(string(res))
}
